package p1;

public class Circle implements Shape {
	private int radius;
	//No Argument
	public Circle() {
		super();
		// TODO Auto-generated constructor stub
	}
	//Parameterized 
	public Circle(int radius) {
		super();
		this.radius = radius;
	}

	public int getRadius() {
		return radius;
	}

	public void setRadius(int radius) {
		this.radius = radius;
	}
	@Override
	public String toString() {
		return "Circle [radius=" + radius + "]";
	}

}
